
function DateConverter(){var a=this;this.serviceName=$("#service_name_hidden").val();if($("#datetimepicker5").length>0){$("#datetimepicker5").datetimepicker({format:"YYYY/MM/DD HH:mm:ss",defaultDate:"2015/01/01 13:00:00"});$("#datetimepicker5").on("dp.change",function(b){a.callWS()});a.callWS()}}DateConverter.prototype.callWS=function(){var c=$("#format_select");var b=$("#source_text");if(c.val()===""||b.val()===""){return}$("#destination_text").val("");$("#destination_text").attr("placeholder","");var a=this;var d=new $.JsonRpcClient({ajaxUrl:"web/web_services/DateConverterWebService.php"});d.call(a.serviceName,{text:$("#source_text").val(),format:$("#format_select").val()},function(e){$("#destination_text").val(e.result_text);if($("#datetimepicker5").length>0){$("#source_textarea_container").removeClass("has-success");$("#source_textarea_container").removeClass("has-error");switch(e.isValid){case true:$("#source_text_container").addClass("has-success");break;case false:$("#source_text_container").addClass("has-error");break}}},function(e){})};$(function(){var a=new DateConverter();$("#format_select").change(function(){a.callWS()});$("#source_text").keyup(function(){a.callWS()})});