
function GlobalMasterPage(){}GlobalMasterPage.prototype.functionalitySearch=function(a){if(a.length<=0){$("#menu *[data-keywords]").show();return}var b=a.toLowerCase().split(" ");$("#menu *[data-keywords]").each(function(){var e=$(this).data("keywords").split(" ");var f=false;for(var d=0;d<b.length&&!f;d++){for(var c=0;c<e.length&&!f;c++){if(e[c].indexOf(b[d])!==-1){f=f||true}}}if(f){$(this).show()}else{$(this).hide()}})};GlobalMasterPage.prototype.toggleMenu=function(){if($("#menu").is(":hidden")){$("#menu").hide();$("#menu").removeClass("hidden-xs")}$("#menu").toggle(function(){if($("#menu").is(":hidden")){$("#menu").addClass("hidden-xs");$("#menu").show()}});$("#content").toggle()};$(function(){var a=new GlobalMasterPage();$("#toggle_menu").click(function(){a.toggleMenu()});$("#search_functionality_text").keyup(function(){a.functionalitySearch($(this).val())})});